import { useState } from "react"
import GameBoard from "./GameBoard"

export default function Player({name,symbol})
{
    const[isEdit,setIsEdit] = useState(false)
    const[pName,setPName] = useState(name)
    const[pSymbol,setPSymbol] = useState(symbol)


    function updatePlayer(val)
    {
        setIsEdit(val)
    }

    function setName(tname)
    {
        setPName(tname)
    }

    function setSymbol(tsymbol)
    {
        setPSymbol(tsymbol)
    }

    return <>
       <li>
        {!isEdit && <span className='player'>
                <span className='player-name'>{pName}</span>
                <span className='player-symbol'>{pSymbol}</span>
                <button onClick={()=>updatePlayer(true)}>Edit</button>
              </span>}

        {isEdit && <span className='player'>
                <input type="text" value={pName} onChange={(e)=>setName(e.target.value)}/>
                <input type="text" value={pSymbol} onChange={(e)=>setSymbol(e.target.value)}/>
                <button onClick={()=>updatePlayer(false)}>Save</button>
              </span>}

        {<GameBoard/>}


        </li> 
    </>
}
