import Header from './components/player/Player';
import "./index.css"
import Player from './components/player/Player';
import GameBoard from "./components/board/GameBoard"
import { useState } from 'react';


function App() {

  const[symbol,setSymbol] = useState('X')

  function switchSymbol()
  {
      setSymbol(symbol==="X"?"O":"X")
  }

  return (
    <>
      <section>
      <menu>
        <div className="game-container">
          <ol id="players">
            <Player name="Player 1" symbol="1" isActive={symbol==="X"}/>
            <Player name="Player 2" symbol="2" isActive={symbol==="O"}/>   
            <GameBoard currPlayerSymbol={symbol} updateSymbol={switchSymbol}/>
          </ol>
        </div>
      </menu>
    </section>

    </>
  );
}

export default App;
