import Header from './components/player/Player';
import "./index.css"
import Player from './components/player/Player';
import GameBoard from "./components/board/GameBoard"
import { useState } from 'react';
import MoveHistory from './components/history/MoveHistory';


function App() {

  const[symbol,setSymbol] = useState('X')
  const[moves,setMoves] = useState([])



  function handleMove(move)
  {
    switchSymbol()
    updateCurrMove(move)
  }

  function switchSymbol()
  {
      setSymbol(symbol==="X"?"O":"X")
  }

  function updateCurrMove(move)
  {
      if(move !== undefined)
      {
          setMoves((moves)=>{return [...moves,move]})
      }
  }

  return (
    <>
      <section>
      <menu>
        <div className="game-container">
          <ol id="players">
            <Player name="Player 1" symbol="1" isActive={symbol==="X"}/>
            <Player name="Player 2" symbol="2" isActive={symbol==="O"}/>   
            <GameBoard currPlayerSymbol={symbol} updateMove={(move)=>handleMove(move)} />
          </ol>
        </div>
      </menu>
      <menu>
        <div className="move-history">
            <MoveHistory currMove={currMove}/>
        </div>
      </menu>
    </section>

    </>
  );
}

export default App;
